import "core-js/modules/es.array.push.js";
import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
export default {
  data() {
    return {
      code: '',
      responseBlocks: [],
      // 当前显示的输出内容
      selectedOption: 'option1',
      options: [{
        value: 'option1',
        label: '基本路径测试'
      }, {
        value: 'option2',
        label: '路径覆盖测试'
      }, {
        value: 'option3',
        label: 'GPT测试'
      }],
      savedRecords: [],
      // 存储保存的输出记录
      currentRecordId: null,
      // 当前显示的记录ID
      // 新增的数据属性
      isPythonModalVisible: false,
      // 控制Python代码运行模态窗口的显示
      pythonCode: '',
      // 存储用户输入的Python代码
      pythonOutput: '',
      // 存储运行结果
      pythonError: '' // 存储运行错误信息
    };
  },
  methods: {
    autoResize() {
      const element = this.$refs.codeInput;
      element.style.height = 'auto';
      element.style.height = element.scrollHeight + 'px';
    },
    async submitCode() {
      if (this.code.trim() === '') return;
      const currentCode = this.code;
      try {
        const processResponse = await fetch('http://127.0.0.1:5001/api/process_code', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            code: this.code,
            option: this.selectedOption
          })
        });
        if (processResponse.ok) {
          const result = await processResponse.json();
          const newBlock = {
            code: currentCode,
            flowchartPath: `http://127.0.0.1:5001${result.flowchartPath}`,
            selectedOption: this.selectedOption // 保存选中的测试方法
          };
          if (this.selectedOption === 'option1' || this.selectedOption === 'option2') {
            newBlock.testCases = result.testCases;
          } else if (this.selectedOption === 'option3') {
            newBlock.responseText = result.responseText;
          }
          this.responseBlocks.push(newBlock);
          this.code = '';
          this.resetTextareaHeight();
        } else {
          const error = await processResponse.json();
          console.error('Error processing code:', error.error);
          alert(`Error: ${error.error}`);
        }
      } catch (error) {
        console.error('Error submitting code:', error);
        alert('提交代码时发生错误，请检查控制台以获取详细信息。');
      }
    },
    saveRecord() {
      if (this.responseBlocks.length === 0) return; // 如果没有输出，不保存
      const newRecord = {
        id: Date.now(),
        // 使用时间戳作为唯一ID
        name: `记录 ${this.savedRecords.length + 1}`,
        responseBlocks: [...this.responseBlocks] // 保存当前的输出内容
      };
      this.savedRecords.push(newRecord);
      this.currentRecordId = newRecord.id;
      alert('记录已保存');
    },
    loadRecord(recordId) {
      const record = this.savedRecords.find(r => r.id === recordId);
      if (record) {
        this.currentRecordId = record.id;
        this.responseBlocks = [...record.responseBlocks];
      }
    },
    deleteRecord(recordId) {
      const index = this.savedRecords.findIndex(r => r.id === recordId);
      if (index !== -1) {
        if (confirm('确定要删除此记录吗？')) {
          this.savedRecords.splice(index, 1);
          // 如果删除的是当前显示的记录，清空显示
          if (this.currentRecordId === recordId) {
            this.currentRecordId = null;
            this.responseBlocks = [];
          }
          alert('记录已删除');
        }
      }
    },
    async downloadRecord(record) {
      // 创建一个HTML字符串，包含图片和文本内容
      let htmlContent = `
        <html>
          <head>
            <meta charset="UTF-8">
            <title>${record.name}</title>
            <style>
              body { font-family: Arial, sans-serif; padding: 20px; }
              .section { margin-bottom: 20px; }
              .section img { max-width: 100%; height: auto; }
              .section pre { background-color: #f4f4f4; padding: 10px; border-radius: 4px; }
              .test-case { background-color: #f4f4f4; padding: 10px; border-radius: 4px; margin-bottom: 10px; }
              .test-case p { margin: 5px 0; }
              h1 { text-align: center; }
              h2 { color: #333; }
            </style>
          </head>
          <body>
            <h1>${record.name}</h1>
            <p><strong>测试方法:</strong> ${this.getOptionLabel(record.responseBlocks[0]?.selectedOption)}</p>
      `;
      for (const block of record.responseBlocks) {
        htmlContent += `<div class="section">`;

        // 添加流程图图片
        if (block.flowchartPath) {
          try {
            const imageBase64 = await this.convertImageToBase64(block.flowchartPath);
            htmlContent += `<h2>流程图:</h2>`;
            htmlContent += `<img src="data:image/png;base64,${imageBase64}" alt="流程图"/>`;
          } catch (error) {
            console.error('Error fetching image:', error);
            htmlContent += `<p>流程图无法加载。</p>`;
          }
        }

        // 添加代码
        htmlContent += `<h2>代码:</h2>`;
        htmlContent += `<pre style="text-align: left;">${this.escapeHtml(block.code)}</pre>`;

        // 添加测试用例或响应文本
        if (block.testCases && block.testCases.length) {
          htmlContent += `<h2>测试用例:</h2>`;
          for (const [i, testCase] of block.testCases.entries()) {
            htmlContent += `
              <div class="test-case">
                <p><strong>测试用例 ${i + 1}:</strong></p>
                <p>输入: ${this.formatInputs(testCase.inputs)}</p>
                <p>条件: ${testCase.conditions}</p>
              </div>
            `;
          }
        } else if (block.responseText) {
          htmlContent += `<h2>响应文本:</h2>`;
          htmlContent += `<pre style="text-align: left;">${this.escapeHtml(block.responseText)}</pre>`;
        }
        htmlContent += `</div>`;
      }
      htmlContent += `
          </body>
        </html>
      `;

      // 创建Blob并触发下载
      const blob = new Blob([htmlContent], {
        type: 'application/msword'
      });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${record.name}.doc`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    },
    // 将图片URL转换为Base64
    async convertImageToBase64(url) {
      const response = await fetch(url, {
        mode: 'cors'
      });
      const blob = await response.blob();
      return await this.blobToBase64(blob);
    },
    // 将Blob转换为Base64
    blobToBase64(blob) {
      return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onloadend = () => {
          const base64data = reader.result.split(',')[1]; // 去掉前缀
          resolve(base64data);
        };
        reader.onerror = reject;
        reader.readAsDataURL(blob);
      });
    },
    clearScreen() {
      this.responseBlocks = []; // 清空当前显示的输出内容
    },
    resetTextareaHeight() {
      const element = this.$refs.codeInput;
      element.style.height = 'auto';
    },
    formatInputs(inputs) {
      return JSON.stringify(inputs, null, 2).replace(/"/g, "'");
    },
    // 转义HTML特殊字符
    escapeHtml(text) {
      const map = {
        '&': '&amp;',
        '<': '&lt;',
        '>': '&gt;',
        '"': '&quot;',
        "'": '&#039;'
      };
      return text.replace(/[&<>"']/g, function (m) {
        return map[m];
      });
    },
    // 获取选项的标签
    getOptionLabel(value) {
      const option = this.options.find(opt => opt.value === value);
      return option ? option.label : value;
    },
    // 新增的方法：

    // 打开Python代码运行模态窗口
    openPythonModal() {
      this.isPythonModalVisible = true;
      this.pythonCode = '';
      this.pythonOutput = '';
      this.pythonError = '';
    },
    // 自动调整Python代码输入框高度
    autoResizePythonCode() {
      const element = this.$refs.pythonCodeInput;
      element.style.height = 'auto';
      element.style.height = element.scrollHeight + 'px';
    },
    // 运行Python代码
    async runPythonCode() {
      if (this.pythonCode.trim() === '') {
        this.pythonError = '请输入Python代码。';
        return;
      }
      try {
        const response = await fetch('http://127.0.0.1:5001/api/run_python', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            code: this.pythonCode
          })
        });
        if (response.ok) {
          const result = await response.json();
          if (result.output) {
            this.pythonOutput = result.output;
            this.pythonError = '';
          } else if (result.error) {
            this.pythonError = result.error;
            this.pythonOutput = '';
          }
        } else {
          const error = await response.json();
          this.pythonError = error.error || '运行代码时发生错误。';
          this.pythonOutput = '';
        }
      } catch (error) {
        console.error('Error running Python code:', error);
        this.pythonError = '提交代码时发生错误，请检查控制台以获取详细信息。';
        this.pythonOutput = '';
      }
    }
  }
};
